script:
frappe.ui.form.on("Customer", "refresh", function(frm) {
     if (!cur_frm.doc.__islocal) {
        $(frm.fields_dict['rn_customer_vehicles'].wrapper)
            .html(frappe.render_template("customer_vehicle", cur_frm.doc.__onload))
	    .find(".btn-vehicle").on("click", function() {
            	frappe.new_doc("Vehicle");
	    });
    	}
});

frappe.ui.form.on("Contact", "validate", function(frm) {
    if (frm.doc.phone != 10 ) {
        msgprint("Mobile should be equal to ten digit long.");
        validated = false;
    }
});
